$(document).ready(function(){$("input:checkbox:not(.switch, .make-switch), input:radio:not(.switch, .make-switch)").uniform();$(".select-on-check-all").change(function(){$.uniform.update()});$(".grid-view tbody .checker input").change(function(){$.uniform.update()});$(".select2").select2();$(".switch").switcher({copy:{en:{yes:"",no:""}}}).on("change",function(){var a=$(this);a.switcher("setDisabled",true);$.getJSON(a.data("link")+"/"+(a.is(":checked")?"on":"off")+"/"+a.data("id"),function(b){if(b.result==="error"){alert(b.error)}if(a.data("reload")){location.reload()}else{a.switcher("setDisabled",false)}})});$(".nav-tabs-custom .nav a").on("click",function(){sessionStorage.activeTab=$(this).attr("href");sessionStorage.page=window.location.href});if(window.location.href==sessionStorage.page){$('.nav-tabs-custom .nav a[href="'+sessionStorage.activeTab+'"]').tab("show")}});